sourcesContent:
import "../App.css";
import Konva from "../components/Konva";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  Button,
  InputGroup,
  FormControl,
  Modal,
  Form,
  Card,
  Container,
  Row,
  Col,
  Tabs,
  Tab,
  CardColumns,
} from "react-bootstrap";
import { useEffect, useState } from "react";

export default function ControlledTabs({
  summary,
  squares,
  setsquares,
  x_directionResultForClient,
  y_directionResultForClient,
  resultForClientPchat,
  resultPchatWithDirection,
  resultMinChiburWithDirection,
  resultMinChiburNoDirection,
}) {
  const [key, setKey] = useState("מינימום פחת");
  return (
    <>
      <Tabs
        className='tabs'
        id='controlled-tab'
        activeKey={key}
        onSelect={(k) => setKey(k)}
      >
        {Object.entries(summary).map(([objKey, value], i) => (
          // {/* <Tab eventKey="מינימום פחת + כיוון סיב אחיד" title= "מינימום פחת + כיוון סיב אחיד"> */}
          <Tab eventKey={value.title} title={value.title}>
            {/* <Container style={{ textAlign: "right", display:"flex", flexDirection:"column", alignItems:"center"}}> */}
            <Row style={{ direction: "rtl", margin: "0 20px" }}>
              <Card
                className='mr-5 mt-5 '
                border='secondary'
                style={{ width: "100%" }}
              >
                <Card.Body>
                  <Row md={12}>
                    <Col md={3}>
                      <Card.Text style={{ textAlign: "right" }}>


                      <strong> סה"כ גלילים ברוחב 2 מטר </strong>
                        <hr></hr>
                        
                          
                        
                      {value.opt2 === 1 &&
                        value.opt2length <= 25 ? (
                          <>
                              
                               <p>כמות:{value.opt2}</p>
                            <p> באורך: {value.opt2length} מטר </p>
                            </>
                              ) : value.opt2 > 1 &&
                              value.opt2length < 25 ? (
                              <>
                                <p>כמות:{value.opt2}</p>
                                <p> באורך: {value.opt2length} מטר כל אחד </p>
                              </>
                               ) : value.opt2length > 25 ? (
                                <>
                                  <p>
                                    {Math.floor(value.opt2length / 25)} גלילים באורך
                                    25 מטר
                                  </p>
                                  {value.opt2length % 25 ? (
                                    <p>
                                      ובנוסף: גליל אחד באורך {value.opt2length % 25}
                                      מטר
                                    </p>
                                      ) : null}
                                      </>
                                    ) : value.opt2length === 25 ? (
                                      <div>גליל אחד באורך 25 מטר</div>
                                    ) : null}
                                    {/* {square.opt4.amount > 1 */}
                                    {value.opt2 ? <br></br> : <span>0</span>}
                                  


                        {/* <strong> סה"כ גלילים ברוחב 2 מטר </strong>
                        <hr></hr>
                        <>
                          {value.opt2 >= 1 ? (
                            <>
                              {value.opt2length < 25 ? (
                                <>
                                  <span>
                                    כמות:<strong>1</strong>{" "}
                                  </span>
                                  <span>
                                    באורך:<strong>{value.opt2length}מטר</strong>
                                  </span>
                                </>
                              ) : value.opt2length > 25 ? (
                                <>
                                  <div>
                                    <strong>
                                      {Math.floor(value.opt2length / 25)}
                                    </strong>
                                    גלילים באורך 25 מטר
                                  </div>
                                  {value.opt2length % 25 ? (
                                    <span>
                                      ועוד <strong>גליל אחד</strong> באורך
                                      {value.opt2length % 25} מטר
                                    </span>
                                  ) : null}
                                </>
                              ) : (
                                <>
                                  <div>גליל אחד באורך 25 מטר</div>
                                </>
                              )}
                            </>
                          ) : (
                            <>
                              <span>0</span>
                            </>
                          )}
                        </>
                        <br></br> */}
                      </Card.Text>
                    </Col>
                    <Col md={3}>
                      <Card.Text style={{ textAlign: "right" }}>
                      
                      <strong> סה"כ גלילים ברוחב 3 מטר </strong>
                        <hr></hr>
                        {value.opt3 === 1 &&
                        value.opt3length <= 25 ? (
                          <>
                             <p>כמות:{value.opt3}</p>
                            
                            <p> באורך: {value.opt3length} מטר </p>
                            </>
                              ) : value.opt3 > 1 &&
                              value.opt3length < 25 ?
                               (
                              <>
                                <p>כמות:{value.opt3}</p>
                                <p> באורך: {value.opt3length} מטר כל אחד </p>
                              </>
                               ) : value.opt3length > 25 ? (
                                <>
                                  <p>
                                    {Math.floor(value.opt3length / 25)} גלילים באורך
                                    25 מטר
                                  </p>
                                  {value.opt3length % 25 ? (
                                    <p>
                                      ובנוסף: גליל אחד באורך {value.opt3length % 25}
                                      מטר
                                    </p>
                                      ) : null}
                                      </>
                                    ) : value.opt3length === 25 ? (
                                      <div>גליל אחד באורך 25 מטר</div>
                                    ) : null}
                                    {/* {square.opt4.amount > 1 */}
                                    {value.opt3 ? <br></br> : <span>0</span>}
                                  
                      
                        {/* <strong> סה"כ גלילים ברוחב 3 מטר </strong>
                        <hr></hr>
                        {value.opt3 >= 1 ? (
                          <>
                            {value.opt3length < 25 ? (
                              <>
                                <span>
                                  כמות:<strong>1</strong>{" "}
                                </span>
                                <span>
                                  באורך:<strong>{value.opt3length}מטר</strong>
                                </span>
                              </>
                            ) : value.opt3length > 25 ? (
                              <>
                                <div>
                                  <strong>
                                    {Math.floor(value.opt3length / 25)}
                                  </strong>
                                  גלילים באורך 25 מטר
                                </div>
                                {value.opt3length % 25 ? (
                                  <span>
                                    ועוד <strong>גליל אחד</strong> באורך
                                    {value.opt3length % 25} מטר
                                  </span>
                                ) : null}
                              </>
                            ) : (
                              <>
